var field = document.getElementById("translation");
var tense = document.getElementById("tense");

function canonicalise(phrase) {
    return phrase.toLowerCase().replace("á", "a").replace("é", "e").replace("í", "i").replace("ñ", "n").replace("ó", "o").replace("ú", "u").replace("ü", "u");
}

function update() {
    for (const [id, data] of Object.entries(conjtemas)) {
        let temaDiv = document.getElementById(id);
        temaDiv.innerHTML = "";
        if (field.value.length < 2) continue;
        for (const [word, conjdata] of Object.entries(data)) {
            let laid = conjdata[tense.value];
            // im tired ok
            let canon = canonicalise(field.value);
            if (canonicalise(word).includes(canon) || canonicalise(laid[0]).includes(canon) || canonicalise(laid[1]).includes(canon) || canonicalise(laid[2]).includes(canon) || canonicalise(laid[3]).includes(canon) || canonicalise(laid[4]).includes(canon) || canonicalise(laid[5]).includes(canon)) {
                if (!temaDiv.innerHTML) {
                    temaDiv.innerHTML += `<h2>Spanish ${id[2]} Tema ${id.match(/[^-]+$/)[0]}</h2>`;
                }
                temaDiv.innerHTML += `<div class="fit"><h3 class="conj-head">${word}</h2><table><tr><td class="conj-td">${laid[0]}</td><td class="conj-td">${laid[3]}</td></tr><tr><td class="conj-td">${laid[1]}</td><td class="conj-td">${laid[4]}</td></tr><tr><td class="conj-td">${laid[2]}</td><td class="conj-td">${laid[5]}</td></tr></table></div>`;
            }
        }
    }
}

